import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 54.1-829 Authorization from Director required; bond; physical examination; emergency medical services vehicles; physician; and health insurance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-829.md","filePath":"vacode/54.1-829.md"}'),r={name:"vacode/54.1-829.md"},n=i('<h1 id="§-54-1-829-authorization-from-director-required-bond-physical-examination-emergency-medical-services-vehicles-physician-and-health-insurance" tabindex="-1">§ 54.1-829 Authorization from Director required; bond; physical examination; emergency medical services vehicles; physician; and health insurance. <a class="header-anchor" href="#§-54-1-829-authorization-from-director-required-bond-physical-examination-emergency-medical-services-vehicles-physician-and-health-insurance" aria-label="Permalink to &quot;§ 54.1-829 Authorization from Director required; bond; physical examination; emergency medical services vehicles; physician; and health insurance.&quot;">​</a></h1><p>A. No person shall act as a promoter, matchmaker, trainer, boxer, martial artist, or professional wrestler in the Commonwealth without first having obtained authorization for such activity from the Department or sanctioning organization approved by the Director pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-829.1/" target="_blank" rel="noreferrer">54.1-829.1</a> and such authorization remains in full force and effect.</p><p>B. No authorization to act as a promoter shall be granted unless the applicant executes and files with the Department a bond, in such penalty as the Department shall determine through regulation, conditioned on the payment of the fees and penalties imposed by this chapter and for the fulfillment of contracts made with professional contestants in accordance with Department regulations. This subsection shall not apply to a promoter applying to conduct an amateur-only event under the authority of a sanctioning organization approved by the Director pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-829.1/" target="_blank" rel="noreferrer">54.1-829.1</a>.</p><p>C. Each boxer and martial artist shall, and each professional wrestler may, be examined prior to entering the ring by a physician who has been licensed to practice medicine in the Commonwealth for at least five years. The physician shall be appointed by the Department or sanctioning organization and shall certify in writing that the contestant&#39;s physical condition is such that he is physically able to engage in the contest.</p><p>D. No event in which boxers or martial artists are contestants shall be conducted without the continuous presence at ringside of a physician who has been licensed to practice medicine in the Commonwealth for at least five years, and unless an emergency medical services vehicle is at the site of the event.</p><p>E. No boxer or martial artist shall participate in any event unless covered by a health insurance policy with minimum coverage in an amount determined by Department regulation.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0895" target="_blank" rel="noreferrer">895</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0853" target="_blank" rel="noreferrer">853</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0216" target="_blank" rel="noreferrer">216</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0264" target="_blank" rel="noreferrer">264</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',7),o=[n];function s(c,l,h,p,d,m){return a(),t("div",null,o)}const u=e(r,[["render",s]]);export{f as __pageData,u as default};
