import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 54.1-831.01 Boxing, Martial Arts, and Professional Wrestling Advisory Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-831.01.md","filePath":"vacode/54.1-831.01.md"}'),o={name:"vacode/54.1-831.01.md"},s=t('<h1 id="§-54-1-831-01-boxing-martial-arts-and-professional-wrestling-advisory-board" tabindex="-1">§ 54.1-831.01 Boxing, Martial Arts, and Professional Wrestling Advisory Board. <a class="header-anchor" href="#§-54-1-831-01-boxing-martial-arts-and-professional-wrestling-advisory-board" aria-label="Permalink to &quot;§ 54.1-831.01 Boxing, Martial Arts, and Professional Wrestling Advisory Board.&quot;">​</a></h1><p>A. The Boxing, Martial Arts, and Professional Wrestling Advisory Board (the Board) is established as an advisory board, within the meaning of § <a href="http://law.lis.virginia.gov/vacode/2.2-2100/" target="_blank" rel="noreferrer">2.2-2100</a>, in the executive branch of state government to advise the Director on matters relating to boxing, martial arts, and professional wrestling events in the Commonwealth.</p><p>B. The Board shall consist of seven members appointed by the Director as follows: one representative of the sport of boxing; one representative of the sport of professional wrestling; one representative of the sport of martial arts; one representative of either the sport of boxing, martial arts, or professional wrestling; one member who is a martial arts instructor who has obtained the rank of black belt or higher; and two citizen members. All members shall be residents of the Commonwealth. All appointments shall be for terms of four years, except that appointments to fill vacancies shall be for the unexpired terms. No person shall be eligible to serve for more than two successive full terms.</p><p>C. The Board shall elect its chairman and vice-chairman from among its members. The Board shall meet at least once each year to conduct its business and upon the call of the Director or chair of the Board. Four members shall constitute a quorum.</p><p>D. Members of the Board shall receive no compensation for their services, but shall be reimbursed for all reasonable and necessary expenses incurred in the discharge of their duties as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>.</p><p>E. Such staff support as is necessary for the conduct of the Board&#39;s business shall be furnished by the Department.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0853" target="_blank" rel="noreferrer">853</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0522" target="_blank" rel="noreferrer">522</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0216" target="_blank" rel="noreferrer">216</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0264" target="_blank" rel="noreferrer">264</a>.</p>',7),i=[s];function n(l,h,c,d,f,p){return r(),a("div",null,i)}const b=e(o,[["render",n]]);export{m as __pageData,b as default};
