import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 54.1-834 Prohibited activities; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-834.md","filePath":"vacode/54.1-834.md"}'),r={name:"vacode/54.1-834.md"},o=a('<h1 id="§-54-1-834-prohibited-activities-penalties" tabindex="-1">§ 54.1-834 Prohibited activities; penalties. <a class="header-anchor" href="#§-54-1-834-prohibited-activities-penalties" aria-label="Permalink to &quot;§ 54.1-834 Prohibited activities; penalties.&quot;">​</a></h1><p>A. No betting or wagering shall be permitted at an event or exhibition before, during, or after the event in the building where the event is held.</p><p>B. No person shall participate in a sham or fake boxing or martial arts contest. The Department shall have the authority to order, without a hearing, the person controlling the purse to hold the distribution to contestants, promoters, and trainers pending a public hearing by the Department. The Department shall, simultaneously with the issuance of such order to retain the share or purse, institute proceedings for a hearing to determine whether a sham or fake boxing or martial arts contest has occurred.</p><p>C. It shall be a Class 1 misdemeanor for any person to violate this section or any statute or regulation governing the persons or activities regulated pursuant to this chapter.</p><p>D. The third or any subsequent conviction for violating any provision of this section during a 36-month period shall constitute a Class 6 felony.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0895" target="_blank" rel="noreferrer">895</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0216" target="_blank" rel="noreferrer">216</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0264" target="_blank" rel="noreferrer">264</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',6),n=[o];function s(h,l,p,c,d,g){return t(),i("div",null,n)}const f=e(r,[["render",s]]);export{_ as __pageData,f as default};
