import{_ as o,o as a,c as t,z as e,a as s}from"./chunks/framework.9482e208.js";const C=JSON.parse('{"title":"§ 54.1-918 State Corporation Commission to prescribe and enforce rates of pilotage and other charges.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/54.1-918.md","filePath":"vacode/54.1-918.md"}'),r={name:"vacode/54.1-918.md"},n=e("h1",{id:"§-54-1-918-state-corporation-commission-to-prescribe-and-enforce-rates-of-pilotage-and-other-charges",tabindex:"-1"},[s("§ 54.1-918 State Corporation Commission to prescribe and enforce rates of pilotage and other charges. "),e("a",{class:"header-anchor",href:"#§-54-1-918-state-corporation-commission-to-prescribe-and-enforce-rates-of-pilotage-and-other-charges","aria-label":'Permalink to "§ 54.1-918 State Corporation Commission to prescribe and enforce rates of pilotage and other charges."'},"​")],-1),i=e("p",null,"The State Corporation Commission shall prescribe and enforce the rates of pilotage and other charges to be observed in the business of pilotage, but before the Commission fixes or prescribes rates or charges it shall give ten days' notice of the time and place of a hearing by publication in a newspaper of general circulation in each of the Cities of Norfolk, Portsmouth and Newport News. For the purpose of determining the fair basis of such rates and charges, the Commission shall, for the two years next preceding, have access to the books and records of the individual pilots who have no organized association, and of any association of pilots who have an organized association whose rates are to be fixed by the Commission, and shall have the same powers given by law in fixing rates and charges of transportation companies.",-1),c=e("p",null,"The Commission shall fix amounts that will be a fair charge for the service rendered. The Commission shall have due regard for necessary operating expenses, maintenance of, depreciation on, and return on investment in properties used and useful in the business of pilotage, and the rates and charges of pilotage at comparable and competing ports of the United States.",-1),h=e("p",null,"When such rates and charges have been fixed and prescribed by the Commission, they shall be the legal rates and charges of pilotage in Virginia, and shall be enforced as provided by law, and the Commission shall have the power to change or alter rates or charges after notice and hearing as provided in this section.",-1),d=e("p",null,"Code 1950, § 54-562; 1988, c. 765; 1992, c. 10.",-1),l=[n,i,c,h,d];function p(f,m,g,b,u,_){return a(),t("div",null,l)}const w=o(r,[["render",p]]);export{C as __pageData,w as default};
