import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 55.1-1015.1 Civil penalties; attorney fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1015.1.md","filePath":"vacode/55.1-1015.1.md"}'),n={name:"vacode/55.1-1015.1.md"},o=i('<h1 id="§-55-1-1015-1-civil-penalties-attorney-fees" tabindex="-1">§ 55.1-1015.1 Civil penalties; attorney fees. <a class="header-anchor" href="#§-55-1-1015-1-civil-penalties-attorney-fees" aria-label="Permalink to &quot;§ 55.1-1015.1 Civil penalties; attorney fees.&quot;">​</a></h1><p>A. In addition to the penalties and liabilities set forth in §§ <a href="/vacode/55.1-1009.1/">55.1-1009.1</a> and <a href="/vacode/55.1-1015/">55.1-1015</a>, in any action brought under this chapter, if a court finds that a person has willfully engaged in an act or practice in violation of this chapter, the Attorney General may recover for the Literary Fund, upon petition to the court, a civil penalty of not more than $5,000 per violation. For purposes of this section, prima facie evidence of a willful violation may be shown when the Attorney General notifies the alleged violator by certified mail that an act or practice is a violation of this chapter and the alleged violator, after receipt of the notice, continues to engage in the act or practice.</p><p>B. The Attorney General recovering a civil penalty under subsection A, or the appropriate licensing authority or the Commission instituting an enforcement action under § <a href="/vacode/55.1-1015/">55.1-1015</a>, may recover costs and reasonable expenses incurred by it in investigating and preparing the case and attorney fees.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0700" target="_blank" rel="noreferrer">700</a>.</p>',4),r=[o];function c(s,l,p,h,d,f){return t(),a("div",null,r)}const u=e(n,[["render",c]]);export{v as __pageData,u as default};
