import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-1223 Notice to tenants for insecticide or pesticide use.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1223.md","filePath":"vacode/55.1-1223.md"}'),o={name:"vacode/55.1-1223.md"},a=n('<h1 id="§-55-1-1223-notice-to-tenants-for-insecticide-or-pesticide-use" tabindex="-1">§ 55.1-1223 Notice to tenants for insecticide or pesticide use. <a class="header-anchor" href="#§-55-1-1223-notice-to-tenants-for-insecticide-or-pesticide-use" aria-label="Permalink to &quot;§ 55.1-1223 Notice to tenants for insecticide or pesticide use.&quot;">​</a></h1><p>A. The landlord shall give written notice to the tenant no less than 48 hours prior to his application of an insecticide or pesticide in the tenant&#39;s dwelling unit unless the tenant agrees to a shorter notification period. If a tenant requests the application of the insecticide or pesticide, the 48-hour notice is not required. Tenants who have concerns about specific insecticides or pesticides shall notify the landlord in writing no less than 24 hours before the scheduled insecticide or pesticide application. The tenant shall prepare the dwelling unit for the application of insecticides or pesticides in accordance with any written instructions of the landlord and, if insects or pests are found to be present, follow any written instructions of the landlord to eliminate the insects or pests following the application of insecticides or pesticides.</p><p>B. In addition, the landlord shall post notice of all insecticide or pesticide applications in areas of the premises other than the dwelling units. Such notice shall consist of conspicuous signs placed in or upon such premises where the insecticide or pesticide will be applied at least 48 hours prior to the application.</p><p>C. A violation by the tenant of this section may be remedied by the landlord in accordance with § <a href="/vacode/55.1-1248/">55.1-1248</a> or by notice given by the landlord requiring the tenant to remedy in accordance with § <a href="/vacode/55.1-1245/">55.1-1245</a>, as applicable.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0760" target="_blank" rel="noreferrer">760</a>, § 55-248.13:3; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0663" target="_blank" rel="noreferrer">663</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),r=[a];function s(c,l,d,p,h,f){return t(),i("div",null,r)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
