import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-1246 Barring guest or invitee of a tenant.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1246.md","filePath":"vacode/55.1-1246.md"}'),n={name:"vacode/55.1-1246.md"},i=r('<h1 id="§-55-1-1246-barring-guest-or-invitee-of-a-tenant" tabindex="-1">§ 55.1-1246 Barring guest or invitee of a tenant. <a class="header-anchor" href="#§-55-1-1246-barring-guest-or-invitee-of-a-tenant" aria-label="Permalink to &quot;§ 55.1-1246 Barring guest or invitee of a tenant.&quot;">​</a></h1><p>A. A guest or invitee of a tenant may be barred from the premises by the landlord upon written notice served personally upon the guest or invitee of the tenant for conduct on the landlord&#39;s property where the premises are located that violates the terms and conditions of the rental agreement, a local ordinance, or a state or federal law. A copy of the notice shall be served upon the tenant in accordance with this chapter. The notice shall describe the conduct of the guest or invitee that is the basis for the landlord&#39;s action.</p><p>B. In addition to the remedies against the tenant authorized by this chapter, a landlord may apply to the magistrate for a warrant for trespass, provided that the guest or invitee has been served in accordance with subsection A.</p><p>C. The tenant may file a tenant&#39;s assertion, in accordance with § <a href="/vacode/55.1-1244/">55.1-1244</a>, requesting that the general district court review the landlord&#39;s action to bar the guest or invitee.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0359" target="_blank" rel="noreferrer">359</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0390" target="_blank" rel="noreferrer">390</a>, § 55-248.31:01; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0760" target="_blank" rel="noreferrer">760</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),o=[i];function s(c,h,l,d,p,g){return t(),a("div",null,o)}const u=e(n,[["render",s]]);export{_ as __pageData,u as default};
