import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 55.1-125 When nonvested property interest or power of appointment created.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-125.md","filePath":"vacode/55.1-125.md"}'),n={name:"vacode/55.1-125.md"},a=o('<h1 id="§-55-1-125-when-nonvested-property-interest-or-power-of-appointment-created" tabindex="-1">§ 55.1-125 When nonvested property interest or power of appointment created. <a class="header-anchor" href="#§-55-1-125-when-nonvested-property-interest-or-power-of-appointment-created" aria-label="Permalink to &quot;§ 55.1-125 When nonvested property interest or power of appointment created.&quot;">​</a></h1><p>A. Except as provided in subsections B and C and in § <a href="/vacode/55.1-128/">55.1-128</a>, the time of creation of a nonvested property interest or a power of appointment is determined under general principles of property law.</p><p>B. For the purposes of §§ <a href="/vacode/55.1-124/">55.1-124</a> through <a href="/vacode/55.1-129/">55.1-129</a>, if there is a person who alone can exercise a power created by a governing instrument to become the unqualified beneficial owner of (i) a nonvested property interest or (ii) a property interest subject to a power of appointment described in subsection B or C in § <a href="/vacode/55.1-124/">55.1-124</a>, the nonvested property interest or power of appointment is created when the power to become the unqualified beneficial owner terminates.</p><p>C. For the purposes of §§ <a href="/vacode/55.1-124/">55.1-124</a> through <a href="/vacode/55.1-129/">55.1-129</a>, a nonvested property interest or a power of appointment arising from a transfer of property to a previously funded trust or other existing property arrangement is created when the nonvested property interest or power of appointment in the original contribution was created.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0714" target="_blank" rel="noreferrer">714</a>, § 55-12.2; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),i=[a];function p(s,c,d,f,h,l){return t(),r("div",null,i)}const v=e(n,[["render",p]]);export{m as __pageData,v as default};
