import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 55.1-126 Reformation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-126.md","filePath":"vacode/55.1-126.md"}'),r={name:"vacode/55.1-126.md"},i=o('<h1 id="§-55-1-126-reformation" tabindex="-1">§ 55.1-126 Reformation. <a class="header-anchor" href="#§-55-1-126-reformation" aria-label="Permalink to &quot;§ 55.1-126 Reformation.&quot;">​</a></h1><p>Upon the petition of an interested person, a circuit court in the county or city in which the affected property or the greater part of such property is located shall reform a disposition in the manner that most closely approximates the transferor&#39;s manifested plan of distribution and is within the 90 years allowed by subdivision A 2, B 2, or C 2 of § <a href="/vacode/55.1-124/">55.1-124</a> if:</p><ol><li><p>A nonvested property interest or a power of appointment becomes invalid under § <a href="/vacode/55.1-124/">55.1-124</a>;</p></li><li><p>A class gift is not but might become invalid under § <a href="/vacode/55.1-124/">55.1-124</a> and the time has arrived when the share of any class member is to take effect in possession or enjoyment; or</p></li><li><p>A nonvested property interest that is not validated by subdivision A 1 of § <a href="/vacode/55.1-124/">55.1-124</a> can vest but not within 90 years after its creation.</p></li></ol><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0714" target="_blank" rel="noreferrer">714</a>, § 55-12.3; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',4),n=[i];function s(c,p,l,d,f,h){return t(),a("div",null,n)}const v=e(r,[["render",s]]);export{m as __pageData,v as default};
