import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-1302 Term of rental agreement; renewal; security deposits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1302.md","filePath":"vacode/55.1-1302.md"}'),n={name:"vacode/55.1-1302.md"},o=r('<h1 id="§-55-1-1302-term-of-rental-agreement-renewal-security-deposits" tabindex="-1">§ 55.1-1302 Term of rental agreement; renewal; security deposits. <a class="header-anchor" href="#§-55-1-1302-term-of-rental-agreement-renewal-security-deposits" aria-label="Permalink to &quot;§ 55.1-1302 Term of rental agreement; renewal; security deposits.&quot;">​</a></h1><p>A. A landlord shall offer all current and prospective year-round residents a rental agreement with a rental period of not less than one year. Such offer shall contain the same terms and conditions as are offered with shorter term leases, except that rental discounts may be offered by a landlord to residents who enter into a rental agreement for a period of not less than one year.</p><p>B. Upon the expiration of a rental agreement, the agreement shall be automatically renewed for a term of one year with the same terms unless the landlord provides written notice to the tenant of any change in the terms of the agreement at least 60 days prior to the expiration date. In the case of an automatic renewal of a rental agreement for a year-round resident, the security deposit initially furnished by the tenant shall not be increased by the landlord, nor shall an additional security deposit be required.</p><p>C. Except as limited by subsection B, the provisions of § <a href="/vacode/55.1-1226/">55.1-1226</a> shall govern the terms and conditions of security deposits for rental agreements under this chapter.</p><p>1992, c. 709, § 55-248.42:1; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0513" target="_blank" rel="noreferrer">513</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0041" target="_blank" rel="noreferrer">41</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),s=[o];function i(l,d,c,h,f,p){return t(),a("div",null,s)}const g=e(n,[["render",i]]);export{_ as __pageData,g as default};
