import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 55.1-1304 Tenant's obligations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1304.md","filePath":"vacode/55.1-1304.md"}`),i={name:"vacode/55.1-1304.md"},o=n('<h1 id="§-55-1-1304-tenant-s-obligations" tabindex="-1">§ 55.1-1304 Tenant&#39;s obligations. <a class="header-anchor" href="#§-55-1-1304-tenant-s-obligations" aria-label="Permalink to &quot;§ 55.1-1304 Tenant&#39;s obligations.&quot;">​</a></h1><p>In addition to the provisions of the rental agreement, the tenant shall:</p><ol><li><p>Comply with applicable laws affecting manufactured home owners and tenants;</p></li><li><p>Keep and maintain the exterior of the tenant&#39;s manufactured home and manufactured home lot as clean and safe as conditions permit;</p></li><li><p>Place all garbage and other waste in the appropriate receptacles, which shall be provided by the tenant when door-to-door garbage and waste pickup is provided;</p></li><li><p>Use in a reasonable and orderly manner all facilities and appliances in the manufactured home park and require any guest or invitee to do so;</p></li><li><p>Conduct himself and require any guest or invitee to conduct himself in a manner that will not disturb the tenant&#39;s neighbors&#39; peaceful enjoyment of the premises;</p></li><li><p>Abide by all reasonable rules and regulations imposed by the landlord; and</p></li><li><p>In the absence of express written agreement to the contrary, occupy the tenant&#39;s manufactured home only as a dwelling unit.</p></li></ol><p>1975, c. 535, § 55-248.44; 1992, c. 709; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',4),r=[o];function l(s,d,p,c,h,m){return a(),t("div",null,r)}const f=e(i,[["render",l]]);export{_ as __pageData,f as default};
