import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 55.1-1309 Waiver of landlord's right to terminate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1309.md","filePath":"vacode/55.1-1309.md"}`),n={name:"vacode/55.1-1309.md"},i=r('<h1 id="§-55-1-1309-waiver-of-landlord-s-right-to-terminate" tabindex="-1">§ 55.1-1309 Waiver of landlord&#39;s right to terminate. <a class="header-anchor" href="#§-55-1-1309-waiver-of-landlord-s-right-to-terminate" aria-label="Permalink to &quot;§ 55.1-1309 Waiver of landlord&#39;s right to terminate.&quot;">​</a></h1><p>Unless the landlord accepts the rent with reservation, and gives a written notice to the tenant of such acceptance within five business days of receipt of the rent, acceptance of periodic rent payments with knowledge in fact of a material noncompliance by the tenant shall constitute a waiver of the landlord&#39;s right to terminate the rental agreement. Except as provided in § <a href="/vacode/55.1-1250/">55.1-1250</a>, if the landlord has given the tenant written notice that the rent payments have been accepted with reservation, the landlord may accept full payment of all rent payments and still be entitled to receive an order of possession terminating the rental agreement.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0047" target="_blank" rel="noreferrer">47</a>, § 55-248.46:1; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>.</p>',3),o=[i];function l(s,c,d,h,f,p){return t(),a("div",null,o)}const m=e(n,[["render",l]]);export{g as __pageData,m as default};
