import{_ as e,o as t,c as r,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-1405 Transfer of deposits upon purchase.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1405.md","filePath":"vacode/55.1-1405.md"}'),a={name:"vacode/55.1-1405.md"},o=n('<h1 id="§-55-1-1405-transfer-of-deposits-upon-purchase" tabindex="-1">§ 55.1-1405 Transfer of deposits upon purchase. <a class="header-anchor" href="#§-55-1-1405-transfer-of-deposits-upon-purchase" aria-label="Permalink to &quot;§ 55.1-1405 Transfer of deposits upon purchase.&quot;">​</a></h1><p>The current owner of nonresidential rental property shall transfer any security deposits and any accrued interest on the deposits in his possession to the new owner at the time of the transfer of the rental property. If the current owner has entered into a written property management agreement with a managing agent in accordance with the provisions of subsection E of § <a href="/vacode/54.1-2135/">54.1-2135</a>, the current owner shall give written notice to the managing agent requesting payment of such security deposits to the current owner prior to settlement with the new owner. Upon receipt of the written notice, the managing agent shall transfer the security deposits to the current owner and provide written notice to each tenant that his security deposit has been transferred to the new owner in accordance with this section.</p><p>1984, c. 281, § 55-507; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0063" target="_blank" rel="noreferrer">63</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0402" target="_blank" rel="noreferrer">402</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),s=[o];function i(c,h,p,f,d,l){return t(),r("div",null,s)}const g=e(a,[["render",i]]);export{_ as __pageData,g as default};
