import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 55.1-1417 Who may recover rent or possession.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1417.md","filePath":"vacode/55.1-1417.md"}'),t={name:"vacode/55.1-1417.md"},n=i('<h1 id="§-55-1-1417-who-may-recover-rent-or-possession" tabindex="-1">§ 55.1-1417 Who may recover rent or possession. <a class="header-anchor" href="#§-55-1-1417-who-may-recover-rent-or-possession" aria-label="Permalink to &quot;§ 55.1-1417 Who may recover rent or possession.&quot;">​</a></h1><p>Notwithstanding any rule of court to the contrary, (i) any person licensed under the provisions of § <a href="/vacode/54.1-2106.1/">54.1-2106.1</a>, (ii) any property manager or the managing agent of a landlord as defined in § <a href="/vacode/55.1-1200/">55.1-1200</a> pursuant to the written property management agreement, or directors, or by a manager, a general partner, or a trustee, of a partnership, association, corporation, limited liability company, limited partnership, professional corporation, professional limited liability company, registered limited liability partnership, registered limited liability limited partnership, business trust, or family trust to sign pleadings as the agent of the business entity, may obtain a judgment (a) for possession in the general district court for the county or city in which the premises, or part thereof, is situated or (b) for rent or damages, including actual damages for breach of the rental agreement, or for final rent and damages under § <a href="/vacode/8.01-128/">8.01-128</a>, in any general district court where venue is proper under Chapter 5 (§ <a href="/vacode/8.01-257/">8.01-257</a> et seq.) of Title 8.01, against any defendant if the person seeking such judgment had a contractual agreement with the landlord to manage the premises for which rent or possession is due and may prepare, execute, file, and have served on other parties in any general district court a warrant in debt, suggestion for summons in garnishment, garnishment summons, order of possession, writ of eviction, or writ of fieri facias arising out of a landlord-tenant relationship. However, the activities of any such person in court shall be limited by the provisions of § <a href="/vacode/16.1-88.03/">16.1-88.03</a>. However, nothing shall be construed as preventing a nonlawyer from requesting relief from the court as provided by law or statute when such nonlawyer is before the court on one of the actions specified herein.</p><p>1983, c. 8, § 55-246.1; 1989, c. 612; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0452" target="_blank" rel="noreferrer">452</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0665" target="_blank" rel="noreferrer">665</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0667" target="_blank" rel="noreferrer">667</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0338" target="_blank" rel="noreferrer">338</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0365" target="_blank" rel="noreferrer">365</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0563" target="_blank" rel="noreferrer">563</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0190" target="_blank" rel="noreferrer">190</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0221" target="_blank" rel="noreferrer">221</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0180" target="_blank" rel="noreferrer">180</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0700" target="_blank" rel="noreferrer">700</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),o=[n];function s(l,g,c,h,f,p){return r(),a("div",null,o)}const v=e(t,[["render",s]]);export{u as __pageData,v as default};
