import{_ as r,o as a,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 55.1-1425 Written act of reentry to be returned and recorded and certificate of reentry published.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1425.md","filePath":"vacode/55.1-1425.md"}'),n={name:"vacode/55.1-1425.md"},c=e("h1",{id:"§-55-1-1425-written-act-of-reentry-to-be-returned-and-recorded-and-certificate-of-reentry-published",tabindex:"-1"},[t("§ 55.1-1425 Written act of reentry to be returned and recorded and certificate of reentry published. "),e("a",{class:"header-anchor",href:"#§-55-1-1425-written-act-of-reentry-to-be-returned-and-recorded-and-certificate-of-reentry-published","aria-label":'Permalink to "§ 55.1-1425 Written act of reentry to be returned and recorded and certificate of reentry published."'},"​")],-1),i=e("p",null,"When actual reentry is made, the party by or for whom the reentry is made shall return a written act of reentry, sworn to by the sheriff or another authorized officer, to the clerk of the circuit court of the county or city in which the lands or tenements are located. The clerk shall record the written act of reentry in the deed book and shall deliver to the party making the reentry a certificate setting forth the substance of such written act. Such certificate shall be published at least once a week for two months successively in a newspaper published in or nearest to such county or city. Such publication shall be proved by affidavit to the satisfaction of the clerk, who shall record such affidavit in the deed book. Such affidavit shall reference the book and page where the original written act of reentry was recorded. The clerk shall return the original act of reentry to the party entitled to it. The written act of reentry, when recorded, and the record of such written act, or a duly certified copy from such record, shall be evidence, in all cases, of the facts contained therein.",-1),d=e("p",null,[t("Code 1919, § 5536; Code 1950, § 55-245; 2014, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0330",target:"_blank",rel:"noreferrer"},"330"),t("; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),t(".")],-1),h=[c,i,d];function s(l,f,u,p,y,b){return a(),o("div",null,h)}const m=r(n,[["render",s]]);export{w as __pageData,m as default};
