import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 55.1-1501 Form of instrument.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1501.md","filePath":"vacode/55.1-1501.md"}'),n={name:"vacode/55.1-1501.md"},o=r('<h1 id="§-55-1-1501-form-of-instrument" tabindex="-1">§ 55.1-1501 Form of instrument. <a class="header-anchor" href="#§-55-1-1501-form-of-instrument" aria-label="Permalink to &quot;§ 55.1-1501 Form of instrument.&quot;">​</a></h1><p>Any agreement in which a residential ground rent is created shall:</p><ol><li><p>Be reduced to writing;</p></li><li><p>Be in recordable form; and</p></li><li><p>Disclose the date, the names of the parties, the ground rent and any future adjustments to the ground rent, when such rent is payable, the duration of the agreement, and the value of the land at the time the agreement is made. If the parties agree to the amount for which the ground rent may be redeemed, such amount shall also be included in the agreement. Such agreement shall be included as a part of the deed or other instrument of transfer.</p></li></ol><p>1975, c. 363, § 55-79.02; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',4),i=[o];function s(d,h,l,c,m,u){return t(),a("div",null,i)}const p=e(n,[["render",s]]);export{f as __pageData,p as default};
