import{_ as a,o as r,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-1504 Redemption rights.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1504.md","filePath":"vacode/55.1-1504.md"}'),n={name:"vacode/55.1-1504.md"},i=e("h1",{id:"§-55-1-1504-redemption-rights",tabindex:"-1"},[t("§ 55.1-1504 Redemption rights. "),e("a",{class:"header-anchor",href:"#§-55-1-1504-redemption-rights","aria-label":'Permalink to "§ 55.1-1504 Redemption rights."'},"​")],-1),s=e("p",null,"The obligor shall have the right to redeem a residential ground rent at any time after three years from the date the ground rent agreement is made. The redemption shall be effected for the amount agreed upon by the obligor and the obligee or, in the absence of such an agreement, shall be determined by capitalizing the ground rent in effect at the time of redemption, using the average rate on long-term business loans charged by commercial banks in the southeast, as published by the Federal Reserve Board. Upon tender of such amount by the obligor, together with any lawfully collectible arrearages of rent and interest thereon, the obligor may redeem the land from, and shall be entitled to a release from, all obligation to pay ground rent. Such release shall be in recordable form and the cost of recording the same, together with any other charges incidental to it, other than the state transfer tax, shall be paid by the obligor.",-1),h=e("p",null,[t("1975, c. 363, § 55-79.05; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),t(".")],-1),l=[i,s,h];function d(c,g,m,f,b,p){return r(),o("div",null,l)}const y=a(n,[["render",d]]);export{_ as __pageData,y as default};
