import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-1802 Developer to register and file annual report; payment of real estate taxes attributable to the common area.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1802.md","filePath":"vacode/55.1-1802.md"}'),o={name:"vacode/55.1-1802.md"},n=r('<h1 id="§-55-1-1802-developer-to-register-and-file-annual-report-payment-of-real-estate-taxes-attributable-to-the-common-area" tabindex="-1">§ 55.1-1802 Developer to register and file annual report; payment of real estate taxes attributable to the common area. <a class="header-anchor" href="#§-55-1-1802-developer-to-register-and-file-annual-report-payment-of-real-estate-taxes-attributable-to-the-common-area" aria-label="Permalink to &quot;§ 55.1-1802 Developer to register and file annual report; payment of real estate taxes attributable to the common area.&quot;">​</a></h1><p>A. Unless control of the association has been transferred to the members, the developer shall register the association with the Common Interest Community Board within 30 days after recordation of the declaration and thereafter shall ensure that the report required pursuant to § <a href="/vacode/55.1-1835/">55.1-1835</a> has been filed.</p><p>B. Upon the transfer of the common area to the association, the developer shall pay all real estate taxes attributable to the open or common space as defined in § <a href="/vacode/58.1-3284.1/">58.1-3284.1</a> through the date of the transfer to the association.</p><p>1993, c. 956, § 55-509.1; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0733" target="_blank" rel="noreferrer">733</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',4),s=[n];function i(l,h,c,p,d,f){return t(),a("div",null,s)}const u=e(o,[["render",i]]);export{_ as __pageData,u as default};
