import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 55.1-1921 Bond to insure completion of improvements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1921.md","filePath":"vacode/55.1-1921.md"}'),a={name:"vacode/55.1-1921.md"},i=n('<h1 id="§-55-1-1921-bond-to-insure-completion-of-improvements" tabindex="-1">§ 55.1-1921 Bond to insure completion of improvements. <a class="header-anchor" href="#§-55-1-1921-bond-to-insure-completion-of-improvements" aria-label="Permalink to &quot;§ 55.1-1921 Bond to insure completion of improvements.&quot;">​</a></h1><p>A. The declarant shall file with the Common Interest Community Board a bond entered into by the declarant in the sum of 100 percent of the estimated cost of completion, to the extent of the declarant&#39;s obligation as stated in the declaration, of all improvements to the common elements of the condominium labeled in the plat or plats as &quot;NOT YET COMPLETED&quot; or &quot;NOT YET BEGUN&quot; located upon submitted land and which the declarant reasonably believes will not be substantially complete at the time of conveyance of the first condominium unit. Such bond shall be conditioned upon the faithful performance of the declarant&#39;s obligation to complete such improvements in strict conformity with the plans and specifications for the same as described in the declaration.</p><p>B. The declarant shall file with the Common Interest Community Board a bond entered into by the declarant in the sum of 100 percent of the estimated cost of completion of a unit in which a time-share interest is conveyed before the unit has been certified as substantially complete in accordance with subsections A and B of § <a href="/vacode/55.1-1920/">55.1-1920</a>. The bond required by this subsection shall be conditioned upon the faithful performance of the declarant&#39;s obligation to complete such improvements in strict conformity with the plans and specifications for the same as described in the declaration.</p><p>C. All bonds required in this section shall be executed by a surety company authorized to transact business in the Commonwealth or by such other surety as is satisfactory to the Board.</p><p>D. The Board may promulgate reasonable regulations that govern the return of bonds submitted in accordance with this section.</p><p>1977, c. 428, § 55-79.58:1; 1988, c. 15; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0560" target="_blank" rel="noreferrer">560</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0851" target="_blank" rel="noreferrer">851</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0871" target="_blank" rel="noreferrer">871</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',6),r=[i];function s(c,l,h,d,m,p){return t(),o("div",null,r)}const b=e(a,[["render",s]]);export{u as __pageData,b as default};
