import{_ as n,o as a,c as i,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 55.1-1969 Restraints on alienation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1969.md","filePath":"vacode/55.1-1969.md"}'),s={name:"vacode/55.1-1969.md"},r=e("h1",{id:"§-55-1-1969-restraints-on-alienation",tabindex:"-1"},[t("§ 55.1-1969 Restraints on alienation. "),e("a",{class:"header-anchor",href:"#§-55-1-1969-restraints-on-alienation","aria-label":'Permalink to "§ 55.1-1969 Restraints on alienation."'},"​")],-1),o=e("p",null,"If the condominium instruments create any rights of first refusal or other restraints on free alienability of the condominium units, such rights and restraints are void unless the condominium instruments make provision for promptly furnishing to any unit owner or purchaser requesting such rights and restraints a recordable statement certifying to any waiver of, or failure or refusal to exercise, such rights and restraints, in all cases where such waiver, failure, or refusal does in fact occur. Failure or refusal to furnish promptly such a statement in such circumstances in accordance with the provisions of the condominium instruments make all such rights and restraints inapplicable to any disposition of a condominium unit in contemplation of which such statement was requested. Any such statement shall be binding on the unit owners' association, the executive board, and every unit owner. Payment of a fee not exceeding $25 may be required as a prerequisite to the issuance of such a statement if the condominium instruments so provide.",-1),c=e("p",null,[t("1974, c. 416, § 55-79.85; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),t(".")],-1),u=[r,o,c];function l(h,d,m,f,p,_){return a(),i("div",null,u)}const y=n(s,[["render",l]]);export{v as __pageData,y as default};
