import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-1981 Termination of registration.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-1981.md","filePath":"vacode/55.1-1981.md"}'),o={name:"vacode/55.1-1981.md"},r=i('<h1 id="§-55-1-1981-termination-of-registration" tabindex="-1">§ 55.1-1981 Termination of registration. <a class="header-anchor" href="#§-55-1-1981-termination-of-registration" aria-label="Permalink to &quot;§ 55.1-1981 Termination of registration.&quot;">​</a></h1><p>A. In the event that all of the units in the condominium have been disposed of and that all periods for conversion or expansion have expired, the Common Interest Community Board shall issue an order terminating the registration of the condominium.</p><p>B. Notwithstanding any other provision of this chapter, the Common Interest Community Board may administratively terminate the registration of a condominium if:</p><ol><li><p>The declarant has not filed an annual report in accordance with § <a href="/vacode/55.1-1979/">55.1-1979</a> for three or more consecutive years; or</p></li><li><p>The declarant&#39;s registration with the State Corporation Commission, if applicable, has not been active for five or more consecutive years.</p></li></ol><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0481" target="_blank" rel="noreferrer">481</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0797" target="_blank" rel="noreferrer">797</a>, § 55-79.93:2; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),n=[r];function s(c,l,h,d,m,p){return t(),a("div",null,n)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
