import{_ as a,o,c as i,z as t,a as e}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 55.1-2143 Tort and contract liability.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2143.md","filePath":"vacode/55.1-2143.md"}'),n={name:"vacode/55.1-2143.md"},r=t("h1",{id:"§-55-1-2143-tort-and-contract-liability",tabindex:"-1"},[e("§ 55.1-2143 Tort and contract liability. "),t("a",{class:"header-anchor",href:"#§-55-1-2143-tort-and-contract-liability","aria-label":'Permalink to "§ 55.1-2143 Tort and contract liability."'},"​")],-1),s=t("p",null,"Neither the association nor any proprietary lessee except the declarant is liable for that declarant's torts in connection with any part of the cooperative that that declarant has the responsibility to maintain. Otherwise, an action alleging wrongdoing by the association shall be brought against the association and not against any proprietary lessee. If such wrongdoing occurred during any period of declarant control, and the association gives the declarant reasonable notice of and an opportunity to defend against the action, the declarant who then controlled the association is liable to the association or to any proprietary lessee (i) for all tort losses not covered by insurance suffered by the association or that proprietary lessee and (ii) for all costs that the association would not have incurred but for a breach of contract or other wrongful act or omission. Whenever the declarant is liable to the association under this section, the declarant is also liable for all litigation expenses, including reasonable attorney fees, incurred by the association. Any statute of limitation affecting the association's right of action under this section is tolled until the period of declarant control terminates.",-1),c=t("p",null,[e("A proprietary lessee is not precluded from bringing an action contemplated by this section because he is a proprietary lessee or a member or officer of the association. Liens resulting from judgments against the association are governed by § "),t("a",{href:"/vacode/55.1-2151/"},"55.1-2151"),e(".")],-1),l=t("p",null,[e("1982, c. 277, § 55-468; 2019, c. "),t("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),e(".")],-1),d=[r,s,c,l];function h(p,f,u,b,g,y){return o(),i("div",null,d)}const v=a(n,[["render",h]]);export{m as __pageData,v as default};
