import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 55.1-2147 Annual budget; reserves for capital components.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2147.md","filePath":"vacode/55.1-2147.md"}'),n={name:"vacode/55.1-2147.md"},i=r('<h1 id="§-55-1-2147-annual-budget-reserves-for-capital-components" tabindex="-1">§ 55.1-2147 Annual budget; reserves for capital components. <a class="header-anchor" href="#§-55-1-2147-annual-budget-reserves-for-capital-components" aria-label="Permalink to &quot;§ 55.1-2147 Annual budget; reserves for capital components.&quot;">​</a></h1><p>A. Except to the extent provided in the declaration, the board of directors shall, prior to the commencement of the fiscal year, make available to lot owners either (i) the annual budget of the association or (ii) a summary of such annual budget.</p><p>B. Except to the extent otherwise provided in the declaration, the executive board shall:</p><ol><li><p>Conduct at least once every five years a study to determine the necessity and amount of reserves required to repair, replace, and restore the capital components as defined in § <a href="/vacode/55.1-2100/">55.1-2100</a>;</p></li><li><p>Review the results of that study at least annually to determine if reserves are sufficient; and</p></li><li><p>Make any adjustments the executive board deems necessary to maintain reserves, as appropriate.</p></li></ol><p>C. To the extent that the reserve study conducted in accordance with this section indicates a need to budget for reserves, the association budget shall include:</p><ol><li><p>The current estimated replacement cost, estimated remaining life, and estimated useful life of the capital components as defined in § <a href="/vacode/55.1-2100/">55.1-2100</a>;</p></li><li><p>As of the beginning of the fiscal year for which the budget is prepared, the current amount of accumulated cash reserves set aside to repair, replace, or restore the capital components and the amount of the expected contribution to the reserve fund for that fiscal year;</p></li><li><p>A statement describing the procedures used for estimation and accumulation of cash reserves pursuant to this section and the extent to which the association is funding its reserve obligations consistent with the study currently in effect; and</p></li><li><p>A statement of the amount of reserves recommended in the study and the amount of current cash for replacement reserves.</p></li></ol><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0436" target="_blank" rel="noreferrer">436</a>, § 55-471.1; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0033" target="_blank" rel="noreferrer">33</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0044" target="_blank" rel="noreferrer">44</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',7),o=[i];function s(c,l,d,p,h,u){return t(),a("div",null,o)}const v=e(n,[["render",s]]);export{m as __pageData,v as default};
