import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 55.1-2177 Receipt of application; order or registration.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2177.md","filePath":"vacode/55.1-2177.md"}'),i={name:"vacode/55.1-2177.md"},o=r('<h1 id="§-55-1-2177-receipt-of-application-order-or-registration" tabindex="-1">§ 55.1-2177 Receipt of application; order or registration. <a class="header-anchor" href="#§-55-1-2177-receipt-of-application-order-or-registration" aria-label="Permalink to &quot;§ 55.1-2177 Receipt of application; order or registration.&quot;">​</a></h1><p>A. The Common Interest Community Board shall acknowledge receipt of an application for registration within five business days after receiving it. Within 60 days after receiving the application, the Common Interest Community Board shall determine whether:</p><ol><li><p>The application and the proposed public offering statement satisfy the requirements of this chapter and the Common Interest Community Board&#39;s regulations;</p></li><li><p>The declaration and bylaws comply with this chapter; and</p></li><li><p>It is likely that the improvements the declarant has undertaken to make can be completed as represented.</p></li></ol><p>B. If the Common Interest Community Board makes a favorable determination, it shall issue promptly an order registering the cooperative. Otherwise, unless the declarant has consented in writing to a delay, the Common Interest Community Board shall issue promptly an order rejecting registration.</p><p>1982, c. 277, § 55-499; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),n=[o];function s(p,l,c,d,h,m){return t(),a("div",null,n)}const g=e(i,[["render",s]]);export{f as __pageData,g as default};
