import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 55.1-2248 Cancellation of cease and desist order; reinstatement of registration of developer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2248.md","filePath":"vacode/55.1-2248.md"}'),a={name:"vacode/55.1-2248.md"},n=o('<h1 id="§-55-1-2248-cancellation-of-cease-and-desist-order-reinstatement-of-registration-of-developer" tabindex="-1">§ 55.1-2248 Cancellation of cease and desist order; reinstatement of registration of developer. <a class="header-anchor" href="#§-55-1-2248-cancellation-of-cease-and-desist-order-reinstatement-of-registration-of-developer" aria-label="Permalink to &quot;§ 55.1-2248 Cancellation of cease and desist order; reinstatement of registration of developer.&quot;">​</a></h1><p>A. The Board shall stipulate to the developer or reseller the reason for any cease and desist order, or revocation of registration as outlined in § <a href="/vacode/55.1-2247/">55.1-2247</a>, by no later than the time such order or revocation is to become effective.</p><p>B. Should the developer or reseller satisfy the Board that it has corrected the reasons for the cease and desist order or revocation of registration, then the Board shall promptly cancel such order or reinstate the registration, and thereafter the developer or reseller may continue its offering or disposition of time-shares.</p><p>1981, c. 462, § 55-397; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',4),i=[n];function s(d,l,c,f,h,p){return t(),r("div",null,i)}const g=e(a,[["render",s]]);export{v as __pageData,g as default};
