import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-2405 Escheator to hold inquest; notice of inquest.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2405.md","filePath":"vacode/55.1-2405.md"}'),r={name:"vacode/55.1-2405.md"},i=a('<h1 id="§-55-1-2405-escheator-to-hold-inquest-notice-of-inquest" tabindex="-1">§ 55.1-2405 Escheator to hold inquest; notice of inquest. <a class="header-anchor" href="#§-55-1-2405-escheator-to-hold-inquest-notice-of-inquest" aria-label="Permalink to &quot;§ 55.1-2405 Escheator to hold inquest; notice of inquest.&quot;">​</a></h1><p>On receiving a list compiled pursuant to § <a href="/vacode/55.1-2404/">55.1-2404</a>, or upon information from any person, in writing and under oath, that any of the conditions described in § <a href="/vacode/55.1-2404/">55.1-2404</a> exists, the escheator shall proceed to hold his inquest to determine whether any land identified has escheated to the Commonwealth. He shall (i) post notice of the time of taking such inquest at the front door of the courthouse for 30 days prior to the inquest and (ii) advertise once in a newspaper of general circulation within the county or city at least seven but not more than 30 days prior to the inquest. Notice shall also be mailed to the last owner of record, if any, as it appears in the tax records of the local treasurer. The escheator shall send a copy of the newspaper advertisement to the State Treasurer prior to the date of the inquest. The inquest shall be held in the same calendar year in which the list or information is received by the escheator. The attorney for the Commonwealth shall act as attorney for this proceeding.</p><p>Code 1919, § 493; Code 1950, § 55-172; 1977, c. 583; 1982, c. 486; 1984, c. 315; 1988, c. 377; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),n=[i];function s(h,c,l,d,f,u){return t(),o("div",null,n)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
