import{_ as t,o,c as n,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 55.1-2409 Proceedings to claim land escheated.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2409.md","filePath":"vacode/55.1-2409.md"}'),s={name:"vacode/55.1-2409.md"},r=e("h1",{id:"§-55-1-2409-proceedings-to-claim-land-escheated",tabindex:"-1"},[a("§ 55.1-2409 Proceedings to claim land escheated. "),e("a",{class:"header-anchor",href:"#§-55-1-2409-proceedings-to-claim-land-escheated","aria-label":'Permalink to "§ 55.1-2409 Proceedings to claim land escheated."'},"​")],-1),l=e("p",null,"When the verdict on an inquest is for the Commonwealth, any person claiming any interest in the lands, whether legal or equitable, may, before the sale of such land, petition the circuit court for redress. The petition shall be accompanied by a bond with good security to pay the Commonwealth all past due real estate taxes, penalties, and interest on such lands. The escheator shall be the sole defendant on behalf of the Commonwealth, and may appear on his own behalf. The escheator shall file an answer stating the objections to the claim. The cause shall be heard, without any unnecessary delay, upon the petition and answer and the evidence. Upon a judgment in favor of the claimant, he shall pay all past due taxes, penalties, and interest. Upon entry of such judgment, the court may award attorney fees to the escheator. For real estate assessment purposes, the commissioner of the revenue or assessor shall continue to assess any escheated property.",-1),c=e("p",null,[a("Code 1919, § 497; Code 1950, § 55-176; 1983, c. 482; 1990, c. 938; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),a(".")],-1),i=[r,l,c];function h(d,p,m,u,f,_){return o(),n("div",null,i)}const b=t(s,[["render",h]]);export{y as __pageData,b as default};
