import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 55.1-2519 Responsibilities of general receiver and clerk.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2519.md","filePath":"vacode/55.1-2519.md"}'),t={name:"vacode/55.1-2519.md"},o=i('<h1 id="§-55-1-2519-responsibilities-of-general-receiver-and-clerk" tabindex="-1">§ 55.1-2519 Responsibilities of general receiver and clerk. <a class="header-anchor" href="#§-55-1-2519-responsibilities-of-general-receiver-and-clerk" aria-label="Permalink to &quot;§ 55.1-2519 Responsibilities of general receiver and clerk.&quot;">​</a></h1><p>The general receiver, if one has been appointed, and the clerk of each circuit court shall be responsible for identifying moneys held by them in their respective accounts that have remained unclaimed by the owner for more than one year after such moneys became payable and for petitioning the court to remit such money to the administrator. There shall be no obligation to report or remit funds deposited as compensation and damages in condemnation proceedings pursuant to § <a href="/vacode/25.1-237/">25.1-237</a> prior to a final court order or pursuant to § <a href="/vacode/33.2-1019/">33.2-1019</a>.</p><p>1988, c. 841, § 55-210.9:2; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0733" target="_blank" rel="noreferrer">733</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),n=[o];function c(s,l,d,p,h,f){return r(),a("div",null,n)}const m=e(t,[["render",c]]);export{g as __pageData,m as default};
