import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 55.1-2525 Notices to be published by administrator.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2525.md","filePath":"vacode/55.1-2525.md"}'),n={name:"vacode/55.1-2525.md"},o=a('<h1 id="§-55-1-2525-notices-to-be-published-by-administrator" tabindex="-1">§ 55.1-2525 Notices to be published by administrator. <a class="header-anchor" href="#§-55-1-2525-notices-to-be-published-by-administrator" aria-label="Permalink to &quot;§ 55.1-2525 Notices to be published by administrator.&quot;">​</a></h1><p>A. The administrator shall cause to be published notice of the report filed under subsection D of § <a href="/vacode/55.1-2524/">55.1-2524</a> once each year in a newspaper of general circulation in the area in which the last known address of any person to be named in the notice is located. If no address is listed or if the address is outside of the Commonwealth, the notice shall be published in the area in which the holder of the abandoned property has his principal place of business.</p><p>B. The published notice shall be entitled &quot;Commonwealth of Virginia Unclaimed Property List&quot; and shall contain:</p><ol><li><p>The names in alphabetical order and account numbers of persons listed in the report and entitled to notice within the area as specified in subsection A; and</p></li><li><p>A statement that information concerning the amount or description of the property and the name and address of the holder may be obtained by any persons possessing an interest in the property by addressing an inquiry to the administrator.</p></li></ol><p>C. The administrator is not required to publish in such notice any item of less than $100 unless he deems such publication to be in the public interest.</p><p>1960, c. 330, § 55-210.13; 1981, c. 47; 1983, c. 190; 1987, c. 236; 1988, c. 378; 1990, c. 460; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0733" target="_blank" rel="noreferrer">733</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',6),r=[o];function s(c,l,d,h,p,b){return t(),i("div",null,r)}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
