import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 55.1-2534 Judicial review of decision of administrator.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2534.md","filePath":"vacode/55.1-2534.md"}'),r={name:"vacode/55.1-2534.md"},o=a('<h1 id="§-55-1-2534-judicial-review-of-decision-of-administrator" tabindex="-1">§ 55.1-2534 Judicial review of decision of administrator. <a class="header-anchor" href="#§-55-1-2534-judicial-review-of-decision-of-administrator" aria-label="Permalink to &quot;§ 55.1-2534 Judicial review of decision of administrator.&quot;">​</a></h1><p>Any person aggrieved by an act or decision of the administrator with respect to a claim for property held by the administrator pursuant to the provisions of this chapter may commence an action in the circuit court of the county or city in which the property claimed is situated to establish his claim. The proceeding shall be brought within three years after the decision of the administrator or, if the administrator fails to act, within three years from the filing of the claim.</p><p>1960, c. 330, § 55-210.22; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0733" target="_blank" rel="noreferrer">733</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0745" target="_blank" rel="noreferrer">745</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),n=[o];function c(s,d,h,l,f,_){return i(),t("div",null,n)}const u=e(r,[["render",c]]);export{m as __pageData,u as default};
