import{_ as a,o as e,c as s,O as t}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 55.1-2810 Damages for trespass by animals; punitive and double damages.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2810.md","filePath":"vacode/55.1-2810.md"}'),n={name:"vacode/55.1-2810.md"},o=t('<h1 id="§-55-1-2810-damages-for-trespass-by-animals-punitive-and-double-damages" tabindex="-1">§ 55.1-2810 Damages for trespass by animals; punitive and double damages. <a class="header-anchor" href="#§-55-1-2810-damages-for-trespass-by-animals-punitive-and-double-damages" aria-label="Permalink to &quot;§ 55.1-2810 Damages for trespass by animals; punitive and double damages.&quot;">​</a></h1><p>A. If any domesticated livestock enters into any grounds enclosed by a lawful fence, as defined in §§ <a href="/vacode/55.1-2804/">55.1-2804</a> through <a href="/vacode/55.1-2807/">55.1-2807</a>, the owner or manager of any such animal shall be liable for the actual damages sustained.</p><p>B. Punitive damages may be awarded but shall not exceed $20 in any case.</p><p>C. For every second and subsequent trespass, the owner or manager of such animal shall be liable for double damages, both actual and punitive.</p><p>Code 1950, §§ 8-874 through 8-876; 1977, c. 624, § 55-306; 1979, c. 486; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',5),r=[o];function d(i,l,c,m,p,u){return e(),s("div",null,r)}const f=a(n,[["render",d]]);export{h as __pageData,f as default};
