import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 55.1-2828 Petition for action to fix boundaries of village or unincorporated community.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2828.md","filePath":"vacode/55.1-2828.md"}'),a={name:"vacode/55.1-2828.md"},n=i('<h1 id="§-55-1-2828-petition-for-action-to-fix-boundaries-of-village-or-unincorporated-community" tabindex="-1">§ 55.1-2828 Petition for action to fix boundaries of village or unincorporated community. <a class="header-anchor" href="#§-55-1-2828-petition-for-action-to-fix-boundaries-of-village-or-unincorporated-community" aria-label="Permalink to &quot;§ 55.1-2828 Petition for action to fix boundaries of village or unincorporated community.&quot;">​</a></h1><p>Twenty or more landowners residing within the boundaries referred to in § <a href="/vacode/55.1-2827/">55.1-2827</a> may file a petition signed by them requesting that the boundaries of such village or unincorporated community be fixed for the purposes of § <a href="/vacode/55.1-2827/">55.1-2827</a>. Notice of the intention to file such petition, stating the date on which the petition will be filed, and such notice shall be (i) posted at the front door of the courthouse of such county, and at three or more conspicuous places within such boundaries and (ii) published once a week for two successive weeks in a newspaper having a general circulation in the county where the village or unincorporated community is located, at least 10 days before the day on which such petition is to be presented. Such petition shall state with reasonable certainty the boundaries within which it is desired to prohibit such animals from running at large, that at least 300 persons reside within such boundaries, and that a majority of the landowners residing therein are in favor of prohibiting such animals from running at large.</p><p>Code 1950, § 8-894; 1977, c. 624, § 55-324; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),r=[n];function s(c,h,d,l,u,f){return o(),t("div",null,r)}const _=e(a,[["render",s]]);export{m as __pageData,_ as default};
