import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-2831 Order of court.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2831.md","filePath":"vacode/55.1-2831.md"}'),a={name:"vacode/55.1-2831.md"},i=r('<h1 id="§-55-1-2831-order-of-court" tabindex="-1">§ 55.1-2831 Order of court. <a class="header-anchor" href="#§-55-1-2831-order-of-court" aria-label="Permalink to &quot;§ 55.1-2831 Order of court.&quot;">​</a></h1><p>The court shall enter an order fixing the boundaries of any village or unincorporated community having within defined boundaries a population of 300 or more for the purpose of preventing domesticated livestock from running at large within such boundaries if (i) in the case of a contested petition, it appears from the evidence or from a report, if any is required pursuant to § <a href="/vacode/55.1-2830/">55.1-2830</a>, that at least 300 persons reside within the boundaries referred to in a petition filed pursuant to § <a href="/vacode/55.1-2828/">55.1-2828</a> and that a majority of the landowners residing therein are in favor of prohibiting domesticated livestock from running at large or (ii) in the case of an uncontested petition, on the basis of the evidence presented in the petition itself.</p><p>Code 1950, § 8-897; 1977, c. 624, § 55-327; 1979, c. 486; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),n=[i];function s(d,c,f,h,p,l){return t(),o("div",null,n)}const m=e(a,[["render",s]]);export{_ as __pageData,m as default};
