import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-2838 Larceny of timber; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-2838.md","filePath":"vacode/55.1-2838.md"}'),n={name:"vacode/55.1-2838.md"},o=r('<h1 id="§-55-1-2838-larceny-of-timber-penalty" tabindex="-1">§ 55.1-2838 Larceny of timber; penalty. <a class="header-anchor" href="#§-55-1-2838-larceny-of-timber-penalty" aria-label="Permalink to &quot;§ 55.1-2838 Larceny of timber; penalty.&quot;">​</a></h1><p>A. Any person who knowingly and willfully takes, steals, and removes from the lands of another any timber growing, standing, or lying on the lands is guilty of larceny. Any person so convicted shall be ordered to pay restitution calculated pursuant to § <a href="/vacode/55.1-2836/">55.1-2836</a>.</p><p>B. In a criminal prosecution pursuant to subsection A, it shall be prima facie evidence of the intent to steal the timber if the timber was harvested or removed from property marked with readily visible paint marks not more than 100 feet apart on trees or posts along the property line, where the paint marks were vertical lines at least two inches in width and at least eight inches in length and the center of the mark was not less than three feet nor more than six feet from the ground or normal water surface.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0604" target="_blank" rel="noreferrer">604</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>, § 55-334.1; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',4),i=[o];function l(s,c,h,p,d,f){return t(),a("div",null,i)}const g=e(n,[["render",l]]);export{_ as __pageData,g as default};
