import{_ as n,o,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 55.1-304 Relocation of easement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-304.md","filePath":"vacode/55.1-304.md"}'),i={name:"vacode/55.1-304.md"},s=e("h1",{id:"§-55-1-304-relocation-of-easement",tabindex:"-1"},[t("§ 55.1-304 Relocation of easement. "),e("a",{class:"header-anchor",href:"#§-55-1-304-relocation-of-easement","aria-label":'Permalink to "§ 55.1-304 Relocation of easement."'},"​")],-1),r=e("p",null,"The owner of land that is subject to an easement for the purpose of ingress and egress may relocate the easement, on the servient estate, by recording in the office of the clerk of the circuit court of the county or city in which the easement or any part of such easement is located, a written agreement evidencing the consent of all affected persons and setting forth the new location of the easement. In the absence of such written agreement, the owner of the land that is subject to such easement may seek relocation of the easement on the servient estate upon petition to the circuit court and notice to all parties in interest. The petition shall be granted if, after a hearing held, the court finds that (i) the relocation will not result in economic damage to the parties in interest, (ii) there will be no undue hardship created by the relocation, and (iii) the easement has been in existence for not less than 10 years.",-1),c=e("p",null,[t("Code 1919, § 5168; Code 1950, § 55-50; 1992, c. 373; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),t(".")],-1),h=[s,r,c];function l(d,f,m,p,u,_){return o(),a("div",null,h)}const v=n(i,[["render",l]]);export{b as __pageData,v as default};
