import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 55.1-308 Private roads; public use; maintenance and improvements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-308.md","filePath":"vacode/55.1-308.md"}'),o={name:"vacode/55.1-308.md"},n=r('<h1 id="§-55-1-308-private-roads-public-use-maintenance-and-improvements" tabindex="-1">§ 55.1-308 Private roads; public use; maintenance and improvements. <a class="header-anchor" href="#§-55-1-308-private-roads-public-use-maintenance-and-improvements" aria-label="Permalink to &quot;§ 55.1-308 Private roads; public use; maintenance and improvements.&quot;">​</a></h1><p>Notwithstanding any provision of a recorded deed or plat to the contrary, a private road serving a subdivision of 50 or fewer lots may be dedicated for public use and may be taken into the secondary state highway system, subject to the provisions and requirements set forth in §§ <a href="/vacode/33.2-335/">33.2-335</a> and <a href="/vacode/33.2-336/">33.2-336</a>, if the owner of the fee interest in such private road obtains the written consent of every lot owner in the subdivision whose lot is served by the private road and the holder of any restrictive covenant or easement rights over and concerning the private road prior to making such dedication and before requirements for acceptance of the road into the secondary state highway system are met. Such consent shall be recorded in the land records of the clerk&#39;s office of the circuit court of the county in which the private road is located.</p><p>2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0495" target="_blank" rel="noreferrer">495</a>, § 55-50.4; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),i=[n];function s(c,d,h,p,l,v){return t(),a("div",null,i)}const u=e(o,[["render",s]]);export{m as __pageData,u as default};
