import{_ as o,o as r,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 55.1-327 Sales under deeds of trust that contain no maturity date or provision authorizing sale.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-327.md","filePath":"vacode/55.1-327.md"}'),s={name:"vacode/55.1-327.md"},n=e("h1",{id:"§-55-1-327-sales-under-deeds-of-trust-that-contain-no-maturity-date-or-provision-authorizing-sale",tabindex:"-1"},[t("§ 55.1-327 Sales under deeds of trust that contain no maturity date or provision authorizing sale. "),e("a",{class:"header-anchor",href:"#§-55-1-327-sales-under-deeds-of-trust-that-contain-no-maturity-date-or-provision-authorizing-sale","aria-label":'Permalink to "§ 55.1-327 Sales under deeds of trust that contain no maturity date or provision authorizing sale."'},"​")],-1),i=e("p",null,"When any property, real or personal, is conveyed by deed of trust to a trustee to secure the payment of a debt, money, notes, bonds, stocks, or other evidences of debt and there is no date fixed for the maturity thereof and such deed of trust contains no provision authorizing the trustee to make sale of such property, or any part thereof, and the reinvestment of the proceeds of sale in other property subject to the terms of such deed of trust, the circuit court, or such court having jurisdiction of the subject matter, upon a complaint filed by any one or more of the lien debtors, in which complaint all persons interested in such lien and all holders of the evidences of debt secured by the deed of trust thereon, and all other necessary or proper parties, except the plaintiffs, shall be made defendants, may order a sale of such property, or any part thereof, and may invest the proceeds of sale under order of court subject to the terms of the deed of trust, provided that (i) the complaint sets forth facts that will justify the sale of the property, to be verified by the affidavit of at least one of the plaintiffs, (ii) no order shall be made authorizing such sale unless it is shown to the satisfaction of the court that the interests of the lien debtor or debtors will be promoted and the interests of no person holding the evidences of debt secured by the deed of trust will be violated thereby, and (iii) the plaintiff or the party for whose benefit the action is brought shall bear the cost.",-1),d=e("p",null,[t("1932, p. 77; Michie Code 1942, § 5167a; Code 1950, § 55-61; 2019, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712",target:"_blank",rel:"noreferrer"},"712"),t(".")],-1),h=[n,i,d];function l(c,f,u,p,m,b){return r(),a("div",null,h)}const _=o(s,[["render",l]]);export{v as __pageData,_ as default};
