import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 55.1-600 When and where writings recorded.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-600.md","filePath":"vacode/55.1-600.md"}'),i={name:"vacode/55.1-600.md"},n=t('<h1 id="§-55-1-600-when-and-where-writings-recorded" tabindex="-1">§ 55.1-600 When and where writings recorded. <a class="header-anchor" href="#§-55-1-600-when-and-where-writings-recorded" aria-label="Permalink to &quot;§ 55.1-600 When and where writings recorded.&quot;">​</a></h1><p>Except when it is otherwise provided, the circuit court of any county or city, or the clerk of any such court, or his duly qualified deputy, in his office, shall record any such writing as to any person whose name is signed thereto with an original signature, when it shall have been acknowledged by him, or proved by two witnesses as to him in such court, or before such clerk, or his duly qualified deputy, in his office, or the manner prescribed in Articles 2 (§ <a href="/vacode/55.1-612/">55.1-612</a> et seq.), 3 (§ <a href="/vacode/55.1-616/">55.1-616</a> et seq.), and 4 (§ <a href="/vacode/55.1-624/">55.1-624</a> et seq.). When such writing is signed by a person acting on behalf of another, or in any representative capacity, the signature of such representative may be acknowledged or proved in the same manner.</p><p>Code 1919, § 5204; Code 1950, § 55-106; 1972, c. 130; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0338" target="_blank" rel="noreferrer">338</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),o=[n];function s(c,h,d,l,f,p){return r(),a("div",null,o)}const u=e(i,[["render",s]]);export{g as __pageData,u as default};
