import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 55.1-602 Presumption that recorded writings are in proper form.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-602.md","filePath":"vacode/55.1-602.md"}'),a={name:"vacode/55.1-602.md"},i=o('<h1 id="§-55-1-602-presumption-that-recorded-writings-are-in-proper-form" tabindex="-1">§ 55.1-602 Presumption that recorded writings are in proper form. <a class="header-anchor" href="#§-55-1-602-presumption-that-recorded-writings-are-in-proper-form" aria-label="Permalink to &quot;§ 55.1-602 Presumption that recorded writings are in proper form.&quot;">​</a></h1><p>A writing that is not properly notarized in accordance with the laws of the Commonwealth shall not invalidate the underlying document; however, any such writing shall not be in proper form for recordation. All recorded writings shall be presumed to be in proper form for recording after having been recorded, and conclusively presumed to be in proper form for recording after having been recorded for a period of three years, except in cases of fraud.</p><p>1973, c. 161, § 55-106.2; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0117" target="_blank" rel="noreferrer">117</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0814" target="_blank" rel="noreferrer">814</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),n=[i];function c(p,s,d,l,f,h){return r(),t("div",null,n)}const m=e(a,[["render",c]]);export{g as __pageData,m as default};
