import{_ as e,o as t,c as n,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 55.1-620 Meaning of \\"acknowledged before me.\\".","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-620.md","filePath":"vacode/55.1-620.md"}'),a={name:"vacode/55.1-620.md"},o=r('<h1 id="§-55-1-620-meaning-of-acknowledged-before-me" tabindex="-1">§ 55.1-620 Meaning of &quot;acknowledged before me.&quot;. <a class="header-anchor" href="#§-55-1-620-meaning-of-acknowledged-before-me" aria-label="Permalink to &quot;§ 55.1-620 Meaning of &quot;acknowledged before me.&quot;.&quot;">​</a></h1><p>For the purposes of this article, &quot;acknowledged before me&quot; means:</p><ol><li><p>That the person acknowledging appeared before the person taking the acknowledgment;</p></li><li><p>That the person acknowledging acknowledged he executed the instrument;</p></li><li><p>That, in the case of:</p></li></ol><p>a. A natural person acknowledging, he executed the instrument for the purposes stated in the instrument;</p><p>b. A corporation, the officer or agent acknowledged he held the position or title set forth in the instrument and certificate, he signed the instrument on behalf of the corporation by proper authority, and the instrument was the act of the corporation for the purpose stated in the instrument;</p><p>c. A partnership, the partner or agent acknowledged he signed the instrument on behalf of the partnership by proper authority and he executed the instrument as the act of the partnership for the purposes stated in the instrument;</p><p>d. A person acknowledging as principal by an attorney-in-fact, he executed the instrument by proper authority as the act of the principal for the purposes stated in the instrument; or</p><p>e. A person acknowledging as a public officer, trustee, administrator, guardian, conservator, or other representative, he signed the instrument by proper authority and he executed the instrument in the capacity and for the purposes stated in the instrument; and</p><ol start="4"><li>That the person taking the acknowledgment either knew or had satisfactory evidence that the person acknowledging was the person named in the instrument or certificate.</li></ol><p>1970, c. 719, § 55-118.5; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0801" target="_blank" rel="noreferrer">801</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',10),i=[o];function s(h,p,c,d,l,f){return t(),n("div",null,i)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
