import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 55.1-630 Acknowledgment taken by trustee in deed of trust; later date.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-630.md","filePath":"vacode/55.1-630.md"}'),o={name:"vacode/55.1-630.md"},n=r('<h1 id="§-55-1-630-acknowledgment-taken-by-trustee-in-deed-of-trust-later-date" tabindex="-1">§ 55.1-630 Acknowledgment taken by trustee in deed of trust; later date. <a class="header-anchor" href="#§-55-1-630-acknowledgment-taken-by-trustee-in-deed-of-trust-later-date" aria-label="Permalink to &quot;§ 55.1-630 Acknowledgment taken by trustee in deed of trust; later date.&quot;">​</a></h1><p>Any certificate of acknowledgment of any deed of trust, taken and certified prior to July 1, 1995, by a person named as trustee therein who was, at the time of taking the acknowledgment, an officer authorized by law to take acknowledgments of deeds, is declared to be as valid and of the same force and effect as if such person had not been a trustee in the deed of trust. Subject to the provisions of § <a href="/vacode/55.1-602/">55.1-602</a>, however, this section shall not affect any right or remedy of any third party that accrued after the recordation of the deed of trust and before July 1, 1995.</p><p>1948, p. 392; Michie Suppl. 1948, § 5207b1; Code 1950, § 55-125; 1952, c. 109; 1956, c. 706; 1958, c. 218; 1962, c. 367; 1966, cc. 137, 492; 1968, c. 4; 1972, c. 631; 1976, c. 685; 1980, c. 143; 1984, c. 35; 1989, c. 602; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0048" target="_blank" rel="noreferrer">48</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),d=[n];function c(i,s,f,l,h,u){return t(),a("div",null,d)}const m=e(o,[["render",c]]);export{p as __pageData,m as default};
