import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 55.1-652 Certificates of commencement of case in bankruptcy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/55.1-652.md","filePath":"vacode/55.1-652.md"}'),c={name:"vacode/55.1-652.md"},o=a('<h1 id="§-55-1-652-certificates-of-commencement-of-case-in-bankruptcy" tabindex="-1">§ 55.1-652 Certificates of commencement of case in bankruptcy. <a class="header-anchor" href="#§-55-1-652-certificates-of-commencement-of-case-in-bankruptcy" aria-label="Permalink to &quot;§ 55.1-652 Certificates of commencement of case in bankruptcy.&quot;">​</a></h1><p>Certificates of commencement of case signed by clerks of bankruptcy courts or clerks of United States district courts, issued pursuant to the acts of Congress relating to bankruptcy, may be filed with the clerk of the court authorized to record deeds for the county or city in which the property of the debtor, for which such certificate has been issued, is located. Such certificate shall be recorded in the deed books and properly indexed in the name of the trustee in bankruptcy in the grantee index and the debtor in the grantor index. For such recordation, the clerk shall receive a fee as prescribed in subdivision A 2 of § <a href="/vacode/17.1-275/">17.1-275</a>.</p><p>1988, c. 100, § 55-142.01; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0432" target="_blank" rel="noreferrer">432</a>; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0712" target="_blank" rel="noreferrer">712</a>.</p>',3),i=[o];function n(s,f,d,h,l,p){return t(),r("div",null,i)}const m=e(c,[["render",n]]);export{_ as __pageData,m as default};
