import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 56-1.2:1 Retail sale of electricity in connection with the provision of electric vehicle charging service.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-1.2:1.md","filePath":"vacode/56-1.2:1.md"}'),c={name:"vacode/56-1.2:1.md"},a=r('<h1 id="§-56-1-2-1-retail-sale-of-electricity-in-connection-with-the-provision-of-electric-vehicle-charging-service" tabindex="-1">§ 56-1.2:1 Retail sale of electricity in connection with the provision of electric vehicle charging service. <a class="header-anchor" href="#§-56-1-2-1-retail-sale-of-electricity-in-connection-with-the-provision-of-electric-vehicle-charging-service" aria-label="Permalink to &quot;§ 56-1.2:1 Retail sale of electricity in connection with the provision of electric vehicle charging service.&quot;">​</a></h1><p>A. The provision of electric vehicle charging service by a person, locality, park authority created by a locality pursuant to § <a href="/vacode/15.2-5702/">15.2-5702</a>, school board, or any agency as defined in § <a href="/vacode/2.2-128/">2.2-128</a> that is not a public utility, public service corporation, or public service company shall not constitute the retail sale of electricity if:</p><ol><li><p>The electricity furnished in connection with the provision of electric vehicle charging service is used solely for transportation purposes; and</p></li><li><p>The person, locality, park authority created by a locality pursuant to § <a href="/vacode/15.2-5702/">15.2-5702</a>, school board, or agency as defined in § <a href="/vacode/2.2-128/">2.2-128</a> providing the electric vehicle charging service has procured the furnished electricity from the public utility that is authorized by the Commission to engage in the retail sale of electricity within the exclusive service territory in which the electric vehicle charging service is provided.</p></li></ol><p>B. The provision of electric vehicle charging service shall:</p><ol><li><p>Be a permitted electric utility activity of a certificated electric utility; and</p></li><li><p>Not affect the status as a public utility of a certificated public utility that provides such service.</p></li></ol><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0408" target="_blank" rel="noreferrer">408</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0239" target="_blank" rel="noreferrer">239</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0295" target="_blank" rel="noreferrer">295</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0446" target="_blank" rel="noreferrer">446</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0248" target="_blank" rel="noreferrer">248</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0255" target="_blank" rel="noreferrer">255</a>.</p>',6),o=[a];function l(n,h,s,p,f,g){return i(),t("div",null,o)}const u=e(c,[["render",l]]);export{d as __pageData,u as default};
