import{_ as a,o as e,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 56-129.1 Participation in the Federal Railroad Administration Safety and Inspection Program.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-129.1.md","filePath":"vacode/56-129.1.md"}'),r={name:"vacode/56-129.1.md"},o=i('<h1 id="§-56-129-1-participation-in-the-federal-railroad-administration-safety-and-inspection-program" tabindex="-1">§ 56-129.1 Participation in the Federal Railroad Administration Safety and Inspection Program. <a class="header-anchor" href="#§-56-129-1-participation-in-the-federal-railroad-administration-safety-and-inspection-program" aria-label="Permalink to &quot;§ 56-129.1 Participation in the Federal Railroad Administration Safety and Inspection Program.&quot;">​</a></h1><p>The State Corporation Commission shall have the authority to participate in carrying out safety inspection activities in connection with any rule, regulation, order, or standard prescribed by the Secretary of Transportation of the United States under the authority of the Federal Railroad Safety Act (49 U.S.C. § 20101 et seq.) as delegated to the Commonwealth by the Federal Railroad Administration, provided that the Commission shall comply with all the requirements imposed by the United States Code. The Commission shall employ such expert, professional or other assistance as is necessary to carry out the activities authorized by this section. Safety inspectors shall attain the Federal Railroad Administration qualifications necessary to qualify the Commonwealth for federal funds. A maximum of $200,000 paid to the State Corporation Commission under §§ <a href="http://law.lis.virginia.gov/vacode/58.1-2660/" target="_blank" rel="noreferrer">58.1-2660</a> through <a href="http://law.lis.virginia.gov/vacode/58.1-2662/" target="_blank" rel="noreferrer">58.1-2662</a> shall be allocated to this program.</p><p>The Commission shall have the authority to adopt such rules in conformance with the Federal Railroad Safety Act that are necessary for the promulgation of railroad safety within the Commonwealth.</p><p>1981, c. 363; 1987, c. 145; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0114" target="_blank" rel="noreferrer">114</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0157" target="_blank" rel="noreferrer">157</a>.</p>',4),n=[o];function s(l,h,d,c,p,m){return e(),t("div",null,n)}const u=a(r,[["render",s]]);export{_ as __pageData,u as default};
