import{_ as e,o as r,c as o,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 56-231.23 General powers granted.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-231.23.md","filePath":"vacode/56-231.23.md"}'),t={name:"vacode/56-231.23.md"},n=a('<h1 id="§-56-231-23-general-powers-granted" tabindex="-1">§ 56-231.23 General powers granted. <a class="header-anchor" href="#§-56-231-23-general-powers-granted" aria-label="Permalink to &quot;§ 56-231.23 General powers granted.&quot;">​</a></h1><p>Each cooperative formed under this article shall have power to do any and all lawful acts or things including, but not limited to the power:</p><ol><li><p>To produce, generate, gather, store, transport, transmit, distribute, buy and sell energy and energy-related products.</p></li><li><p>To sue and be sued.</p></li><li><p>To have a seal and alter the same at pleasure.</p></li><li><p>To acquire, hold and dispose of property, real and personal, tangible and intangible, or interests therein and to pay therefor in cash or property or on credit, and to secure and procure payment of all or any part of the purchase price thereof on such terms and conditions as the board shall determine.</p></li><li><p>To render service and to acquire, own, operate, maintain and improve a system or systems.</p></li><li><p>To accept gifts or grants of money or of property, real or personal, from any person, municipality or federal agency and to accept voluntary or uncompensated services.</p></li><li><p>To sell, lease, mortgage or otherwise encumber or dispose of all or any parts of its property, as hereinafter provided.</p></li><li><p>To contract debts, borrow money and to issue or assume the payment of bonds, and other obligations.</p></li><li><p>To fix, maintain and collect reasonable fees, rents, tolls and other charges for service rendered.</p></li><li><p>To exercise, with respect to its providing regulated utility service, all the powers set forth in § <a href="http://law.lis.virginia.gov/vacode/56-49/" target="_blank" rel="noreferrer">56-49</a>, including the power of eminent domain as prescribed for other public service corporations by general law.</p></li><li><p>To assist its members and nonmember customers, by loans or otherwise, in the acquisition by them of such installation and wiring, and the obtaining of such machinery, equipment and appliances, as will enable them to secure the greatest benefit from the use of utility services supplied by the cooperative.</p></li><li><p>To issue nonassessable nonvoting common and preferred capital stock or similar securities and pay dividends thereon.</p></li><li><p>To become a member or stockholder in one or more other cooperatives or corporations created to engage in any business not prohibited by law, including, but not limited to, other types of public service company business.</p></li><li><p>To perform any and all of the foregoing acts and do any and all of the foregoing things under, through or by means of its own officers, agents and employees, or by contracts with any person, federal agency or municipality.</p></li></ol><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0874" target="_blank" rel="noreferrer">874</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0944" target="_blank" rel="noreferrer">944</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0999" target="_blank" rel="noreferrer">999</a>.</p>',4),i=[n];function s(l,p,c,d,h,g){return r(),o("div",null,i)}const u=e(t,[["render",s]]);export{m as __pageData,u as default};
