import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 56-231.24 Power to dispose of property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-231.24.md","filePath":"vacode/56-231.24.md"}'),r={name:"vacode/56-231.24.md"},i=a('<h1 id="§-56-231-24-power-to-dispose-of-property" tabindex="-1">§ 56-231.24 Power to dispose of property. <a class="header-anchor" href="#§-56-231-24-power-to-dispose-of-property" aria-label="Permalink to &quot;§ 56-231.24 Power to dispose of property.&quot;">​</a></h1><p>No cooperative may sell, lease or dispose of all or substantially all of its property (other than property which, in the judgment of the board, is neither necessary nor useful in operating and maintaining the cooperative&#39;s system and which in any one year shall not exceed fifty percent in value of the value of all the property of the cooperative, or merchandise), unless authorized to do so by the votes of at least a two-thirds majority of its members; however, a cooperative (i) may mortgage, finance (including, without limitation, pursuant to a sale and leaseback or lease and leaseback transaction), or otherwise encumber its assets by a vote of at least two-thirds of its board of directors; (ii) may sell or transfer its assets to another cooperative upon the vote of a majority of its members at any regular or special meeting if the notice of such meeting contains a copy of the terms of the proposed sale or transfer; (iii) may sell or transfer distribution system facilities to a city or town at any time following the annexation of additional territory pursuant to § <a href="http://law.lis.virginia.gov/vacode/56-265.4:2/" target="_blank" rel="noreferrer">56-265.4:2</a> by a vote of at least two-thirds of its board of directors; or (iv) may sell, lease or dispose of its property to an affiliate pursuant to a plan approved by the Commission in accordance with subsection B of § <a href="http://law.lis.virginia.gov/vacode/56-590/" target="_blank" rel="noreferrer">56-590</a> by a vote of at least two-thirds of the members of the Board.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0874" target="_blank" rel="noreferrer">874</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0944" target="_blank" rel="noreferrer">944</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0999" target="_blank" rel="noreferrer">999</a>.</p>',3),s=[i];function n(l,p,f,c,h,d){return t(),o("div",null,s)}const y=e(r,[["render",n]]);export{_ as __pageData,y as default};
