import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 56-231.38 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-231.38.md","filePath":"vacode/56-231.38.md"}'),r={name:"vacode/56-231.38.md"},a=o('<h1 id="§-56-231-38-definitions" tabindex="-1">§ 56-231.38 Definitions. <a class="header-anchor" href="#§-56-231-38-definitions" aria-label="Permalink to &quot;§ 56-231.38 Definitions.&quot;">​</a></h1><p>As used in this article:</p><p>&quot;Affiliate&quot; means a separate affiliated or subsidiary corporation or other separate legal entity.</p><p>&quot;Board&quot; means any board of directors of a cooperative formed under or which becomes subject to this article.</p><p>&quot;Commission&quot; means the State Corporation Commission of Virginia.</p><p>&quot;Cooperative&quot; means a power supply cooperative formed under the former Power Supply Cooperatives Act (§ <a href="http://law.lis.virginia.gov/vacode/56-231.1/" target="_blank" rel="noreferrer">56-231.1</a> et seq.) or a utility aggregation cooperative formed under this article or which becomes subject to this article.</p><p>&quot;Energy&quot; means and includes all energy, regardless of how or where it is generated or produced.</p><p>&quot;HVACR&quot; means heating, ventilation, air conditioning and refrigeration.</p><p>&quot;Member&quot; means any person that holds any class of membership in a cooperative.</p><p>&quot;Obligations&quot; means all evidences of indebtedness issued by or the payment of which is assumed by a cooperative.</p><p>&quot;Patronage capital&quot; includes all amounts received by a cooperative from the sale of electric power to members in excess of the cooperative&#39;s cost of furnishing electric power to members and such other margins as determined by the Board.</p><p>&quot;Person&quot; means and includes natural persons, firms, associations, cooperatives, corporations, limited liability companies, business trusts, partnerships, limited liability partnerships and bodies politic.</p><p>&quot;Propane or fuel oil equipment&quot; means equipment and related systems to store or use propane or fuel oil products.</p><p>&quot;Regulated utility services&quot; means utility services that are subject to regulation as to rates or service by the Commission.</p><p>&quot;System&quot; means any plant, works, facility, or property used or useful in connection with the purchase, generation, sale or transmission of energy, utility products and services, or both.</p><p>&quot;Traditional cooperative activity&quot; means any business, service or activity in which cooperatives in Virginia have traditionally engaged and that is incidental to and substantially related to the electric utility business conducted by a cooperative on or before July 1, 1999; provided, however, that traditional cooperative activity does not include any program to (i) buy or maintain an inventory of HVACR equipment or household appliances; (ii) install or service any such equipment or household appliances for customers, unless such service is not provided by the cooperative but by a third party individual, firm or corporation licensed to perform such service; (iii) sell HVACR equipment or household appliances to customers metered and billed on residential rates; (iv) sell HVACR equipment to customers other than those metered and billed on residential rates except where such sale is an incidental part of providing other energy services or providing other traditional cooperative activities; (v) sell or distribute propane or fuel oil; sell, install or service propane or fuel oil equipment; or maintain or buy an inventory of propane or fuel oil equipment for resale; or (vi) serve as a coordinator of nonelectric energy services or provide engineering consulting services except when such energy or engineering services are an incidental part of a marketing effort to provide other energy or engineering services or as a part of providing services that are other traditional cooperative activities.</p><p>&quot;Utility services&quot; means any products, services, and equipment related to energy, telecommunications, water and sewerage.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0874" target="_blank" rel="noreferrer">874</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0944" target="_blank" rel="noreferrer">944</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0964" target="_blank" rel="noreferrer">964</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0989" target="_blank" rel="noreferrer">989</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0999" target="_blank" rel="noreferrer">999</a>.</p>',18),n=[a];function s(l,p,c,u,d,h){return i(),t("div",null,n)}const f=e(r,[["render",s]]);export{v as __pageData,f as default};
