import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 56-231.50:2 Right of action; violation of rules or regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-231.50:2.md","filePath":"vacode/56-231.50:2.md"}'),i={name:"vacode/56-231.50:2.md"},r=t('<h1 id="§-56-231-50-2-right-of-action-violation-of-rules-or-regulations" tabindex="-1">§ 56-231.50:2 Right of action; violation of rules or regulations. <a class="header-anchor" href="#§-56-231-50-2-right-of-action-violation-of-rules-or-regulations" aria-label="Permalink to &quot;§ 56-231.50:2 Right of action; violation of rules or regulations.&quot;">​</a></h1><p>A. Any person who suffers loss as the proximate result of a violation by a cooperative or its affiliate of any rule or regulation adopted by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/56-231.50:1/" target="_blank" rel="noreferrer">56-231.50:1</a> shall be entitled to initiate an action to recover actual damages or $500, whichever is greater, and to obtain injunctive relief. Any action pursuant to this section shall be commenced within two years after its accrual.</p><p>B. Notwithstanding any other provision of law to the contrary, in addition to any damages awarded, such person may also be awarded reasonable attorney&#39;s fees and court costs.</p><p>C. In any case arising under this section, no liability shall be imposed upon any cooperative or its affiliate which shows by a preponderance of the evidence that (i) the act or practice alleged to be in violation of any rule or regulation adopted by the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/56-231.50:1/" target="_blank" rel="noreferrer">56-231.50:1</a> was an act or practice over which the same had no control or (ii) the alleged violation resulted from a bona fide error notwithstanding the maintenance of procedures reasonably adopted to avoid a violation. However, nothing in this section shall prevent the court from ordering restitution and payment of reasonable attorney&#39;s fees and court costs pursuant to subsection B to any person aggrieved as a result of an unintentional violation.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0874" target="_blank" rel="noreferrer">874</a>.</p>',5),n=[r];function s(l,c,d,h,f,u){return o(),a("div",null,n)}const v=e(i,[["render",s]]);export{g as __pageData,v as default};
