import{_ as o,o as t,c as i,z as e,a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 56-235.3 Procedures for investigation of rate applications.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-235.3.md","filePath":"vacode/56-235.3.md"}'),s={name:"vacode/56-235.3.md"},n=e("h1",{id:"§-56-235-3-procedures-for-investigation-of-rate-applications",tabindex:"-1"},[a("§ 56-235.3 Procedures for investigation of rate applications. "),e("a",{class:"header-anchor",href:"#§-56-235-3-procedures-for-investigation-of-rate-applications","aria-label":'Permalink to "§ 56-235.3 Procedures for investigation of rate applications."'},"​")],-1),r=e("p",null,"At any hearing on the application of a public utility for a change in a rate, toll, charge or schedule, the burden of proof to show that the proposed change is just and reasonable, shall be upon the public utility. The Commission shall be authorized to prescribe all necessary rules and regulations for the conduct of such hearings which shall provide for full and fair participation in such hearings by any interested person subject to such guidelines as the Commission may deem appropriate. Upon the conclusion of such hearings, the Commission shall issue an order and such opinion as is necessary to set forth fully the Commission's findings of fact and conclusions of law. Copies of the transcripts of public hearings held to establish a fair rate of return and changes in rates, tolls and charges for investor-owned public utilities involving significant public interest shall be placed in no less than one location nor more than three locations in the geographic area served by the utility. The Commission shall determine which proceedings are of sufficient interest to require the placing of such transcripts and the location or locations to be used; provided, however, that proceedings involving investor-owned utilities serving 25,000 or more customers shall be deemed to be of sufficient public interest.",-1),c=e("p",null,"1977, c. 336.",-1),l=[n,r,c];function h(d,p,u,f,g,m){return t(),i("div",null,l)}const _=o(s,[["render",h]]);export{v as __pageData,_ as default};
