import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 56-245.1:2 Customers to be notified of renewable power options.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/56-245.1:2.md","filePath":"vacode/56-245.1:2.md"}'),i={name:"vacode/56-245.1:2.md"},a=r('<h1 id="§-56-245-1-2-customers-to-be-notified-of-renewable-power-options" tabindex="-1">§ 56-245.1:2 Customers to be notified of renewable power options. <a class="header-anchor" href="#§-56-245-1-2-customers-to-be-notified-of-renewable-power-options" aria-label="Permalink to &quot;§ 56-245.1:2 Customers to be notified of renewable power options.&quot;">​</a></h1><p>A. The Commission shall post on its website the names, telephone numbers, and available hyperlinks of suppliers of electric energy licensed to sell retail electric energy pursuant to § <a href="http://law.lis.virginia.gov/vacode/56-587/" target="_blank" rel="noreferrer">56-587</a>, that (i) expressly state in their applications for licensure, or for any renewal thereof, that they offer electric energy supplied from renewable energy to retail customers in the Commonwealth as described in subdivision A 5 of § <a href="http://law.lis.virginia.gov/vacode/56-577/" target="_blank" rel="noreferrer">56-577</a> and (ii) request in any such applications that they be identified on the Commission&#39;s website as making such offers. Provided, however, that by posting such information on its website, the Commission shall not be deemed to provide any guarantees or assurances concerning the bona fides of such offers or that any such offers are in conformance with the laws of the Commonwealth.</p><p>B. At least once each calendar quarter, each investor-owned electric utility in the Commonwealth shall include in or on the customer bills a notice directing them to the Commission website described in subsection A. Each investor-owned electric utility shall also feature available options for purchasing electric energy from renewable sources offered by the utility prominently on its website.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0518" target="_blank" rel="noreferrer">518</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0248" target="_blank" rel="noreferrer">248</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0259" target="_blank" rel="noreferrer">259</a>.</p>',4),n=[a];function s(l,c,h,f,p,d){return t(),o("div",null,n)}const b=e(i,[["render",s]]);export{u as __pageData,b as default};
